import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';

const TITLE = 'Расписание лиги Championship'

const LeagueSchedule = (props) => {
  const [leaguesSchedule, setLeaguesSchedule] = useState([]);

  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  const [search, setSearch] = useState('');
  const [filteredDataLeagues, setFilteredDataLeagues] = useState([]);

  useEffect(() =>{
    let API_KEY = process.env.REACT_APP_USER_TOKEN;
    let league_id = props.match.params.league_id;
    let period = props.period;
    console.log("kkk"+"https://api.football-data.org/v2/competitions/"+league_id+"/teams?season="+period);
    setLoaded(false);
  //  const apiUrl = "https://api.football-data.org/v2/competitionss/"+league_id+"/teams?season="+period;
    const apiUrl = "https://api.football-data.org/v2/competitions/PL/matches?season=2020";
    fetch(apiUrl, {
      method: 'GET',
      headers: {
         'X-Auth-Token': '79c229e5c59445cdb091adf3d2bee52b'
      }
    })
    .then(res => res.json())
    .then(
      json => {
        setLoaded(true);
        setLeaguesSchedule(json);
      },
      (error) =>{
        setLoaded(true);  
        setError(error);
      }
    )
    .catch(error => console.log('parsing failed', error))
  },[])
let Data1=leaguesSchedule.matches;

console.log("eaguesSchedule"+Data1);
if (Data1 !== undefined){
console.log("eaguesSchedule"+Data1.map(item => ("Дата начала:"+item.season.startDate+"Дата окончания:"+item.season.startDate+"   "+"Домашняя группа:"+item.homeTeam.name+"Гость:"+item.homeTeam.name)));
}
  let Data=props.Data;
//const Data=leaguesSchedule;

  useEffect( () =>{
    setFilteredDataLeagues(
      Data.filter( leaguesSchedule => {
        return (leaguesSchedule.match_awayteam_name+leaguesSchedule.match_awayteam_name).toLowerCase().includes(search.toLowerCase())
      })
      )
  },[search])

  
if (Data1 !== undefined){
  const ll=Data1.map(item => (  
    <li key={item.id}>"Дата начала:"+{item.season.startDate}+"Дата окончания:"+{item.season.startDate}+"   "+"Домашняя группа:"+{item.homeTeam.name}+"Гость:"+{item.homeTeam.name})))
    </li> 
  ))}
 console.log("error"+error);
  console.log("loaded"+loaded);
  
	return ( 
    <div className="content">
      <h1>{TITLE}</h1>
      <input type="text" placeholder="Название команды" onChange={ e => setSearch(e.target.value)} />
        <ul>    
          {ll}
        </ul>
    </div>
	)  
}

export default withRouter(LeagueSchedule);
