import React from 'react';
import s from './Periods.module.css';
const PERIODS = ['2000', '2001', '2002', '2003','2004', '2005', '2006', '2007', '2008', '2009', '2010', '2011', '2012','2013', '2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021'];
/*    console.log('props.period'); console.log(props.period); установить по умолчанию*/
const Periods = (props) => {
  const [period, setPeriod] = useState('');

  let periodElement = React.createRef();	
  
  const ListPeriods=PERIODS.map((p) => <option value={p}>{ p }</option>);

  useEffect( () =>{
    props.setPeriod(period);
  },[period])

  /*let selectPeriod = () => {
  	let text = periodElement.current.value; 

  	props.setPeriod(text);
  	
  }*/
 
  return (<div className={s.period}> 
  			<select className="check-period" ref={periodElement} onChange={setPeriod} value={props.period}>
            	{ListPeriods}
          	</select>
          	
          </div>	);
}//Periods


export default Periods;
