      })
  }

  let listItems=<div>Нет данных</div> 

  if (filteredDataLeagues !== null ){
     listItems=filteredDataLeagues.map(item => (  
     <li key={item.id}>"Дата начала:"+{moment(item.utcDate).format('YYYY-MM-DD')}+"Дата окончания:"+{moment(item.lastUpdated).format('YYYY-MM-DD')}+"   "+"Домашняя группа:"+{item.homeTeam.name}+"Гость:"+{item.awayTeam.name})))
     </li> 
  ))
  }
