import {setStartDateTeamSchedule} from "../../../redux/teams-reducer";
import {setEndDateTeamSchedule} from "../../../redux/teams-reducer";

class TeamScheduleContainer extends React.Component {


componentDidUpdate(prevProps) {

  if ((this.props.startDate+this.props.endDate !== prevProps.startDate)||(this.props.endDate+this.props.endDate !== prevProps.endDate)) {

    let team_id = this.props.match.params.team_id;
		let API_KEY = process.env.REACT_APP_USER_TOKEN;
    	
   	let year = this.props.year;
    let dateFrom = moment(this.props.startDate).format('YYYY-MM-DD')
    let dateTo = moment(this.props.endDate).format('YYYY-MM-DD')
   	//setLoaded(false);
    //https://api.football-data.org/v2/teams/66/matches?dateFrom=2019-01-01&dateTo=2019-12-08
   	const apiUrl = "https://api.football-data.org/v2/teams/"+team_id+"/matches?dateFrom="+dateFrom+"&dateTo="+dateTo;
   	fetch(apiUrl, {
     		method: 'GET',
     		headers: {
      	  'X-Auth-Token': API_KEY
     		}
   	})
    .then((response) => response.json())
    .then((response) => {
      this.props.setTeamSchedule(response.matches);
       //this.setState({isLoaded: true});
    })
    .then((error) => {
      this.setState({false: true});
      this.setState({error});
    })    	
    .catch(error => console.log('parsing failed', error))

