import React, {useState, useEffect}  from 'react';
import s from './League.module.css';
import { NavLink } from 'react-router-dom';
import {setLeagueCreator} from "../../../redux/league-reducer";

const League = (props) => {
  /*let refLeagueShedule = React.createRef();  
  let refListTeams = React.createRef();  */
	const [league, setLeague] = useState('');
 
  /*useEffect( () =>{
    props.setLeague("2001");
    console.log("leGUE"+league);
  },[league])*/
 

 let selectLeague = () => {
  //console.log("props.league_id="+props.league_id);
  
   let action = setLeagueCreator(props.league_id);
   props.dispatch(action); 
  }

//console.log("eee"+league);
	return ( <li  className={s.item} key={props.league_id}>
                {props.league_name}

                {league}
                <NavLink to={"/leagueschedule/"+props.league_id+"/"+props.period} onClick={selectLeague}>   Расписание лиги</NavLink>
    
                <NavLink to={"/teams/"+props.league_id+"/"+props.period}  onClick={selectLeague}>        Список команд</NavLink>
           </li>      
	)  
}

export default League;
