import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';

const TITLE = 'Расписание лиги Championship'

const LeagueSchedule = (props) => {

  const [leaguesSchedule, setLeaguesSchedule] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() =>{
  setLoaded(false);
  let API_KEY = process.env.REACT_APP_USER_TOKEN;
  let league_id = props.match.params.league_id;
  fetch("https://apiv2.apifootball.com/?action=get_teams&league_id="+league_id+"&APIkey="+API_KEY)
  .then(res => res.json())
  .then(
    json => {
      setLoaded(true);
      setLeaguesSchedule(json);
    },
    (error) =>{
      setLoaded(true);  
          setError(error);
    }
  )
  .catch(error => console.log('parsing failed', error))
},[])

const ll=leaguesSchedule.map(item => (  
                   <li key={item.match_id}>Дата:{item.match_date} {item.match_time}       (Дом:{item.match_hometeam_name})   :   (Гость:{item.match_awayteam_name})
                   </li> 
                 ))

console.log(leaguesSchedule);
	return ( <div className="content">
              <h1>{TITLE}</h1>
                <ul>    
                 {ll}
          </ul>
        </div>
	)  
}

export default withRouter(LeagueSchedule);
