import React, {useState, useEffect} from 'react';
import Search  from '../../common/Search/Search';
import { withRouter } from 'react-router-dom';
import s from './TeamSchedule.module.css';


 
const TeamSchedule = (props) => {
  const TITLE = 'Расписание команды Swansea   '+props.period +' год';
/*  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() =>{
  setLoaded(false);
  let API_KEY = process.env.REACT_APP_USER_TOKEN;
  let team_id = props.match.params.team_id;
  fetch("https://apiv2.apifootball.com/?action=get_events&from=2020-12-01&to=2021-01-03&team_id="+team_id+"&APIkey="+API_KEY)
  .then(res => res.json())
  .then(
    json => {
      setLoaded(true);
      setTeamSchedule(json);
    },
    (error) =>{
      setLoaded(true);  
          setError(error);
    }
  )
  .catch(error => console.log('parsing failed', error))
  },[])
*/
  let Data=props.Data;
  //const Data=teams;

let filteredDataTeams = null;

if (Data !== undefined){ 
   filteredDataTeams = Data.filter( teamSchedule => {
      return (teamSchedule.match_awayteam_name+teamSchedule.match_date+teamSchedule.match_time).toLowerCase().includes(props.search.toLowerCase())
   })
}
    
let listItem=<div>Нет данных</div> 

if (filteredDataTeams !== null ){
   listItem=filteredDataTeams.map(item => (
    <li key={item.match_id}>Дата:{item.match_date} {item.match_time}       (Гость:{item.match_awayteam_name}) </li> ))
}    

return (
	<div >
    <Search dispatch={props.dispatch}/> 
      <ul>
       	<h2>{TITLE}</h2>
         	{listItem}
      </ul>
  </div>
)
}

export default withRouter(TeamSchedule);
