import React, {useState, useEffect} from 'react';
import s from './Teams.module.css';
import  Team  from './Team/Team';
import { withRouter } from 'react-router-dom';
 
const TITLE = 'Команды лиги Championship';

const Teams = (props) => {
const [teams, setTeams] = useState([]);
const [loaded, setLoaded] = useState(false);
const [error, setError] = useState(null);

useEffect(() =>{
  setLoaded(false);
  let API_KEY = process.env.REACT_APP_USER_TOKEN;
  let league_id = props.match.params.league_id;
  fetch("https://apiv2.apifootball.com/?action=get_teams&league_id="+league_id+"&APIkey="+API_KEY)
  .then(res => res.json())
  .then(
    json => {
      setLoaded(true);
      setTeams(json);
    },
    (error) =>{
      setLoaded(true);  
          setError(error);
    }
  )
  .catch(error => console.log('parsing failed', error))
},[])


const ListTeams = () => {
  let Teams
console.log("teams_error"+teams.error);
  if (teams.error == undefined) { 
    Teams=teams.map(item => ( 
               < Team team_id={item.team_key} team_name={item.team_name}/> ));
              
    }
    else{ 
      <div>Данных нет</div>
    }
    return Teams;
}
if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!loaded) {
      return <div>Loading...</div>;
    } else {
	return(
	 <div >
           <ul>
             <h1>{TITLE}</h1>
             <ListTeams/>
          </ul>
        </div>)
			
}}

export default withRouter(Teams);
