const SET_LEAGUE='SET-LEAGUE';

let initialState=null;

const periodReducer = (state = initialState, action) => {
if (action.type === SET_LEAGUE){
  	state.league=action.league;
	console.log("SET-LEAGUE="+action.league);
}
return state;
}

export const setLeagueCreator = (text) => ({ type: SET_LEAGUE, league:text });

export default periodReducer;
