const ListPeriods=PERIODS.map((p) => <option>{ p }</option>);

const Periods = (props) => {
  const href="/leagues/"+props.period
	return(
    <select name="periodlist" onChange={this.handleEntry}>
            {ListPeriods}
            <option>{props.match.params.per}</option>
          </select>);
}
export default Periods;
