import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';

const TITLE = 'Расписание лиги Championship'

const LeagueSchedule = (props) => {
  const [leaguesSchedule, setLeaguesSchedule] = useState([]);

  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  const [search, setSearch] = useState('');
  const [filteredDataLeagues, setFilteredDataLeagues] = useState([]);

  useEffect(() =>{
    let API_KEY = process.env.REACT_APP_USER_TOKEN;
    let league_id = props.match.params.league_id;
    let period = props.period;
    console.log(API_KEY);
    console.log("https://api.football-data.org/v2/competitions/"+league_id+"/matches?season="+period);
    setLoaded(false);
    const apiUrl = "https://api.football-data.org/v2/competitions/"+league_id+"/matches?season="+period;
    fetch(apiUrl, {
      method: 'GET',
      headers: {
         'X-Auth-Token': API_KEY
      }
    })
    .then(res => res.json())
    .then(
      json => {
        setLoaded(true);
        setLeaguesSchedule(json);
      },
      (error) =>{
        setLoaded(true);  
        setError(error);
      }
    )
    .catch(error => console.log('parsing failed', error))
  },[])
let Data1=leaguesSchedule.matches;

//console.log("eaguesSchedule"+Data1);
if (Data1 !== undefined){
//console.log("eaguesSchedule"+Data1.map(item => ("Дата начала:"+item.season.startDate+"Дата окончания:"+item.season.startDate+"   "+"Домашняя группа:"+item.homeTeam.name+"Гость:"+item.homeTeam.name)));
}
  let Data=props.Data;



  useEffect( () =>{
    if (Data1 !== undefined){
    setFilteredDataLeagues(
      Data1.filter( leaguesSchedule => {
        return (leaguesSchedule.homeTeam.name+leaguesSchedule.awayTeam.name).toLowerCase().includes(search.toLowerCase())
      })
      )}
  },[search])

 let ll=<div>Нет данных</div> 
if (filteredDataLeagues !== ""){
   ll=filteredDataLeagues.map(item => (  
    <li key={item.id}>"Дата начала:"+{item.season.startDate}+"Дата окончания:"+{item.season.startDate}+"   "+"Домашняя группа:"+{item.homeTeam.name}+"Гость:"+{item.awayTeam.name})))
    </li> 
  ))
}
 console.log("error"+filteredDataLeagues);
  console.log("loaded"+loaded);
 
 if (error) {
      return <div>Error: {error.message}</div>;
  } 
  else if (!loaded) {
      return <div>Loading...</div>;
  } 
  else {

	return ( 
    <div className="content">
      <h1>{TITLE}</h1>
      <input type="text" placeholder="Название команды" onChange={ e => setSearch(e.target.value)} />
        <ul>    
          {ll}
        </ul>
    </div>
	) 
}
}

export default withRouter(LeagueSchedule);
