import React from 'react';
//import s from './Period.module.css';
import {setPeriodCreator} from "../../../redux/period-reducer";
import DatePicker from "react-datepicker";
import { connect } from "react-redux";
import {compose} from "redux";
import 'react-datepicker/dist/react-datepicker.css';


const Period = (props) => {	
  

  let setStartDate = (date) => {
    props.selStartDate=setStartDatePeriod(date);	
  }

   let setEndDate = (date) => {
    props.setEndDatePeriod(date); 
  }
 
  return (    
    <div className="MyDateFilter">
      <span>Start Date:</span>
      <DatePicker
        dateFormat="yyyy-MM-dd"
        selected={props.startDate}
        onChange={setStartDate}
        peekNextMonth
        showMonthDropdown
        showYearDropdown
        selectsStart
        startDate = {props.startDate}

      />
      <span>End Date:</span>
      <DatePicker
        dateFormat="yyyy-MM-dd"
        selected={props.endDate}
        onChange={setEndDate}
        peekNextMonth
        showMonthDropdown
        showYearDropdown
        selectsEnd
        endDate = {props.endDate}
      />

    </div> 	);
}

let mapStateToProps = (state) => ({
    startDate : state.period.startDate,
    endDate : state.period.endDate,
})
//export default Period;

export default compose(connect(mapStateToProps,{setStartDatePeriod}),
               connect(mapStateToProps,{setEndDatePeriod})
)(Period);
