import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';
import s from './TeamSchedule.module.css';


const TITLE = 'Расписание команды Swansea';

 
const TeamSchedule = (props) => {
  const [teamSchedule, setTeamSchedule] = useState([]);
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() =>{
  setLoaded(false);
  let API_KEY = process.env.REACT_APP_USER_TOKEN;
  let team_id = props.match.params.team_key;
  fetch("https://apiv2.apifootball.com/?action=get_events&from=2020-12-01&to=2021-01-03&team_id="+team_id+"&APIkey="+API_KEY)
  .then(res => res.json())
  .then(
    json => {
      setLoaded(true);
      setTeamSchedule(json);
    },
    (error) =>{
      setLoaded(true);  
          setError(error);
    }
  )
  .catch(error => console.log('parsing failed', error))
},[])

const kk=teamSchedule.map(item => (  
                  <li key={item.match_id}>Дата:{item.match_date} {item.match_time}       (Гость:{item.match_awayteam_name})
                  </li> ))

	return (
		<div >
           <ul>
            	<h1>{TITLE}</h1>
            	{kk}
           </ul>
        </div>
	)
}

export default withRouter(TeamSchedule);
