import React, { Component } from 'react';
import  Leagues  from './Leagues';
import { connect } from "react-redux";
import {compose} from "redux";
import { withRouter } from 'react-router-dom';
import Preloader from '../common/Preloader/Preloader';
import {setLeagueCreator} from "../../redux/leagues-reducer";

class LeaguesContainer  extends React.Component {

	componentDidMount(){

/*		axios.get('https://apiv2.apifootball.com/?action=get_leagues&APIkey=e2cbc09e57d33b6ab8d17dd2154867fe96ba48b56309658c4b45e82ce6d3cfd9')
		      .then(response => {
		      	this.props.toggleIsFetching(false);
		      	this.props.setUsers(response.data.items);
		      	this.props.setTotalUsersCount(response.data.totalCount);
		      }) ;*/
	}
	
	selLeague = (text) => {
    	this.props.setLeagueCreator(text);
      }

	render() {

	

		return (

			<Leagues leagues = {this.props.listLeagues} period={this.props.period} search={this.props.search} selLeague={ this.selLeague }/>
			)
	}
}

let mapStateToProps = (state) => ({
    listLeagues : state.leagues.listLeagues,
    period:state.period,
    search:state.search
})

export default compose(connect(mapStateToProps,{setLeagueCreator}),
                       withRouter
)(LeaguesContainer);
