const SET_PERIOD='SET-PERIOD';

let initialState='';

const periodReducer = (state = initialState, action) => {
	if (action.type === SET_PERIOD){
		//state.period="2099";
		//return action.period;
		console.log("SET-LEAGUE=2423");

		console.log("SET-LEAGUE="+action.period);
		return action.period;
	}
	return state;
}

export const setPeriodCreator = (text) => ({ type: SET_PERIOD, period:text })

export default periodReducer;
