import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';

const TITLE = 'Расписание лиги Championship'

const LeagueSchedule = (props) => {
  const [leaguesSchedule, setLeaguesSchedule] = useState([]);

  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  const [search, setSearch] = useState('');
  const [filteredDataLeagues, setFilteredDataLeagues] = useState([]);

  useEffect(() =>{
    let API_KEY = process.env.REACT_APP_USER_TOKEN;
    let league_id = props.match.params.league_id;
    let period = props.match.params.period;
    setLoaded(false);
    const apiUrl = "https://api.football-data.org/v2/competitions/"+league_id+"/teams?season="+period;
    fetch(apiUrl, {
      method: 'GET',
      headers: {
         'X-Auth-Token': '79c229e5c59445cdb091adf3d2bee52b'
      }
    })
    .then(res => res.json())
    .then(
      json => {
        setLoaded(true);
        setLeaguesSchedule(json);
      },
      (error) =>{
        setLoaded(true);  
        setError(error);
      }
    )
    .catch(error => console.log('parsing failed', error))
  },[])

  let Data=props.Data;
  //const Data=leaguesSchedule;

  useEffect( () =>{
    setFilteredDataLeagues(
      Data.filter( leaguesSchedule => {
        return (leaguesSchedule.match_awayteam_name+leaguesSchedule.match_awayteam_name).toLowerCase().includes(search.toLowerCase())
      })
      )
  },[search])

  

  const ll=filteredDataLeagues.map(item => (  
    <li key={item.match_id}>Дата:{item.match_date} {item.match_time}       (Дом:{item.match_hometeam_name})   :   (Гость:{item.match_awayteam_name})
    </li> 
  ))

  console.log("kkk"+leaguesSchedule);
	return ( 
    <div className="content">
      <h1>{TITLE}</h1>
      <input type="text" placeholder="Название команды" onChange={ e => setSearch(e.target.value)} />
        <ul>    
          {ll}
        </ul>
    </div>
	)  
}

export default withRouter(LeagueSchedule);
