import React, { Component } from 'react';
import { connect } from "react-redux";
import LeagueSchedule from './LeagueSchedule';
import { withRouter } from 'react-router-dom';
import {setLeagueSchedule} from "../../../redux/leagues-reducer";
import {setlistLeaguesCreator} from "../../../redux/leagues-reducer";
import {setLeagueCreator} from "../../../redux/leagues-reducer";
import {compose} from "redux";

//import leagueScheduleData from '../../../data/EventsOfLeague177';

class LeagueScheduleContainer extends React.Component {

//let league_id = props.match.params.league_id;
	
	componentDidMount(){
		//loaded='false';
    let league_id = this.props.match.params.league_id;
		let API_KEY = process.env.REACT_APP_USER_TOKEN;
    	
    	let year = this.props.year;
    
    	//setLoaded(false);
    	const apiUrl = "https://api.football-data.org/v2/competitions/"+league_id+"/matches?season="+year;

    	fetch(apiUrl, {
      		method: 'GET',
      		headers: {
         	  'X-Auth-Token': API_KEY
      		}
    	})
      .then((response) => response.json())
      .then((response) => {
        console.log({matches:response.matches});
        this.props.setLeagueSchedule(response.matches);
        let league={id:response.competition.code, name: response.competition.name}
        this.props.setLeagueCreator(league);
          //this.setState({isLoaded: true});
      })
      .then((error) => {
          this.setState({false: true});
          this.setState({error});
      })    	
    	.catch(error => console.log('parsing failed', error))

   
//    this.props.setLeagueSchedule(this.props.Data.matches);
   
 
    //action = setLeagueSchedule(Data.matches);
    //this.props.store.dispatch(setLeagueSchedule(leagueScheduleData.matches));  

  	}	


	
		/*if (error) {
      		return <div>Error: {error.message}</div>;
  		} 
  		else if (!loaded) {
      		return <div>Loading...</div>;
  		} 
  		else {*/



    render() {
		  return  <>
        <Period store={props.store} />
				<LeagueSchedule  leagueSchedule = {this.props.leagueSchedule} league_name={this.props.league_name} year={this.props.year} search={this.props.search}/>
			</>
		//}
	}
}

let mapStateToProps = (state) => ({
    leagueSchedule : state.leagues.leagueSchedule,
    league_name : state.leagues.league.name,
    year:state.year,
    search:state.search
})

export default compose( connect(mapStateToProps,{setLeagueCreator}),
                        connect(mapStateToProps,{setLeagueSchedule}),
                withRouter
)(LeagueScheduleContainer);
