import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css';
import s from './TeamSchedule.module.css';


 const TeamSchedule = (props) => {
  const TITLE1 = 'Лига "'+props.league_name+'" '+props.period +' год';
  const TITLE2 = 'Расписание команды "'+props.team_name+'"';
  const [startDate, setStartDate] = useState(new Date("2019-01-08"));
  const [endDate, setEndDate] = useState(new Date("2019-12-10"));
 /* const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() =>{
  setLoaded(false);
