  let team_id = props.match.params.team_id;
  const apiUrl = "https://api.football-data.org/v2/competitions/"+league_id+"/teams?season="+period;
  fetch(apiUrl, {
          method: 'GET',
          headers: {
            'X-Auth-Token': API_KEY
          }
      })
  https://api.football-data.org/v2/competitions/PL/teams?season=2019
  fetch("https://api.football-data.org/v2/competitions/)
  .then(res => res.json())
  .then(
    json => {
      setLoaded(true);
      setTeamSchedule(json);
    },
    (error) =>{
      setLoaded(true);  
          setError(error);
    }
  )
  .catch(error => console.log('parsing failed', error))
  },[])
*/
  let Data=null;
  if ((props.teamSchedule.listSchedule !== null)&(props.teamSchedule.listSchedule !== undefined)){
    Data=props.teamSchedule.listSchedule
  }
  //const Data=teams;

let filteredDataTeams = null;

if ((Data !== null)&(Data !== undefined)){ 
   filteredDataTeams = Data.filter( teamSchedule => {
      return (teamSchedule.awayTeam.name+teamSchedule.homeTeam.name+teamSchedule.utcDate).toLowerCase().includes(props.search.toLowerCase())
   }
   )
}
    
let listItem=<div>Нет данных</div> 

if ((filteredDataTeams !== null )&(filteredDataTeams !== undefined)){
   listItem=filteredDataTeams.map(item => (
    <li key={item.competition.id}>Дата:{item.utcDate} Домашняя группа: {item.homeTeam.name}  (Гость:{item.awayTeam.name}) </li> ))
}    

/*let setStartDate = () => {console.log("StartDate")}
let setEndDate = () => {console.log("EndDate")}
let startDate = () => {console.log("StartDate")}
let endDate = () => {console.log("EndDate")}*/


return (
	<div > 
      <ul>
       	<h2>{TITLE1}</h2>
        <h2>{TITLE2}</h2>
    <div className="MyDateFilter">
      <span>Start Date:</span>
      <DatePicker
        selected={startDate}
        onChange={date => setStartDate(date)}
        selectsStart
        startDate={startDate}
        endDate={endDate}
      />
      <DatePicker
        selected={endDate}
        onChange={date => setEndDate(date)}
        selectsEnd
        startDate={startDate}
        endDate={endDate}
        minDate={startDate}
      />

    </div>
         	{listItem}
      </ul>
  </div>
)
}

export default withRouter(TeamSchedule);

