import React, {useState, useEffect} from 'react';
import s from './Leagues.module.css';
import  League  from './League/League';



const TITLE = 'Список лиг';
const API_KEY = process.env.REACT_APP_USER_TOKEN;

const Leagues = (props) => {
const [leagues, setLeagues] = useState([]);
const [loaded, setLoaded] = useState(false);
const [error, setError] = useState(null);


useEffect(() =>{
	setLoaded(false);
	fetch("https://apiv2.apifootball.com/?action=get_leagues&APIkey="+API_KEY)
	.then(res => res.json())
	.then(
		json => {
		  setLoaded(true);
		  setLeagues(json);
		},
		(error) =>{
		  setLoaded(true);	
          setError(error);
		}
	)
	.catch(error => console.log('parsing failed', error))
},[])






//let Data=props.Data;
let Data=leagues;

console.log("Data");
console.log(Data);

let ListLeagues = () => {
	if ( Data.length>0) {
	Data.map(item => ( 
               < League league_id={ item.league_id } league_name={ item.league_name } period={ props.period }/> ));}
    else{ 

    	alert("1");
    	<div>Даныых нет</div>}
}

if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!loaded) {
      return <div>Loading...</div>;
    } else {
    
	return( <div >
            <h2>{ TITLE }</h2>
           
            <h2>{ props.period }</h2>
            <div className={ s.list_items }>         
                {ListLeagues} 
            </div>
        </div>
  )//return      
}}

export default Leagues;
