import React, { Component } from 'react';
import LeagueSchedule from './LeagueSchedule';

class LeaguesScheduleContainer  extends React.Component {

	
	componentDidMount(){
		loaded=false;
		let API_KEY = process.env.REACT_APP_USER_TOKEN;
    	let league_id = props.match.params.league_id;
    	let period = props.period;
    	//console.log("https://api.football-data.org/v2/competitions/"+league_id+"/matches?season="+period);
    	//setLoaded(false);
    	const apiUrl = "https://api.football-data.org/v2/competitions/"+league_id+"/matches?season="+period;

    	fetch(apiUrl, {
      		method: 'GET',
      		headers: {
         		'X-Auth-Token': API_KEY
      		}
    	})
    	.then(res => res.json())
    	.then(
      		json => {
        		loaded=true; 
        		setLeaguesSchedule(json);
      		},
      		(error) =>{
        		loaded=true; 
        		err=error;
      		}
    	)
    	.catch(error => console.log('parsing failed', error))
  	
}
/*
		axios.get('https://apiv2.apifootball.com/?action=get_leagues&APIkey=e2cbc09e57d33b6ab8d17dd2154867fe96ba48b56309658c4b45e82ce6d3cfd9')
		      .then(response => {
		      	this.props.toggleIsFetching(false);
		      	this.props.setUsers(response.data.items);
		      	this.props.setTotalUsersCount(response.data.totalCount);
		      }) ;
	}
*/
	render() {
		if (error) {
      		return <div>Error: {error.message}</div>;
  		} 
  		else if (!loaded) {
      		return <div>Loading...</div>;
  		} 
  		else {
		  return (
				<LeaguesSchedule {...this.props}/>
			)
		}
	}
}
export default LeaguesScheduleContainer;
