const SET_YEAR='SET-PERIOD';

let initialState={ startDate:new Date("2000/02/08"),
    			   endDate:new Date("2021/02/08")};

const periodReducer = (state = initialState, action) => {
  switch(action.type){
    case SET_START_DATE_TEAM_SCHEDULE:{
      return {
        ...state,
        teamSchedule:{
          ...state.teamSchedule,
          startDate:action.startDate
        }        
      }
    }
    case SET_END_DATE_TEAM_SCHEDULE:{
      return {
        ...state,
        teamSchedule:{
          ...state.teamSchedule,
          endDate:action.endDate
        }        
      }
    }
   }
	return state;
}

export const setYearCreator = (text) => ({ type: SET_PERIOD, year:text })

export default yearReducer;
