import React from 'react';
import s from './Periods.module.css';
import {setPeriodCreator} from "../../../redux/period-reducer";
const PERIODS = ['2000', '2001', '2002', '2003','2004', '2005', '2006', '2007', '2008', '2009', '2010', '2011', '2012','2013', '2014', '2015', '2016', '2017', '2018', '2019', '2020', '2021'];


const Periods = (props) => {
  //let periodElement = React.createRef();	
  
  const ListPeriods=PERIODS.map((p) => <option value={p}>{ p }</option>);

  let selectPeriod = (e) => {
    let text = e.target.value; 
    let action = setPeriodCreator(text);
  	props.dispatch(action); 
    //setPeriod(text);	
  }
 
  return (<div className={s.period}> 
  			<select className={s.check_period} onChange={selectPeriod} value={props.period}>
            	{ListPeriods}
          	</select>
          	
          </div>	);
}//Periods


export default Periods;
