import React from 'react';
//import logo from './logo.svg';
import './App.css';

import { BrowserRouter, Route,  NavLink } from 'react-router-dom';

import Leagues from './pages/Leagues/Leagues';
import Teams from './pages/Teams/Teams';
import LeagueSchedule from './pages/LeagueSchedule/LeagueSchedule';
import TeamSchedule from './pages/TeamSchedule/TeamSchedule';

//import { ReactComponent as LogoLeagues } from './img/league.png';
import { ReactComponent as LogoTeams } from './img/teams.png';
import { ReactComponent as LogoSchedule } from './img/schedule.png';

import Navbar from './components/Navbar/Navbar';
import  Periods  from './components/Periods/Periods'

const SECTIONS = [
    { title: 'Список лиг', component: Leagues, href: '/leagues', Icon: LogoLeagues },
    { title: 'Расписание лиги' , component: LeagueSchedule, href: '/leagueSchedule', Icon:LogoSchedule  },
    { title: 'Команды', component: Teams, href: '/teams', Icon: LogoTeams },
    { title: 'Расписание команды', component: TeamSchedule, href: '/teamSchedule', Icon: LogoSchedule },
  ];

class App extends React.Component { 
  constructor(){
    super();
     this.state={period:'2021'
                }
  }//constructor

  render(){
    const per=this.state.period;
    console.log({per});
    return(
      <BrowserRouter>    
        <div className="add-wrapper">
          <Navbar/>  
          <Periods/>
          <div className="add-wrapper-content">
              <Route path='/leagues/:per' component={Leagues}/>
              <Route path='/leagueSchedule' component={LeagueSchedule}/>
              <Route path='/teams' component={Teams}/>
              <Route path='/teamSchedule' component={TeamSchedule}/>
          </div>
        </div>
      </BrowserRouter>  
    );//return  
  };//render
}//class

export default App;
