import React from 'react';
import { withRouter } from 'react-router-dom';
import DatePicker from "react-datepicker";
import getDate from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css';
import s from './TeamSchedule.module.css';
import Moment from 'moment';


 const TeamSchedule = (props) => {
  const TITLE1 = 'Лига "'+props.league_name+'" '+props.period +' год';
  const TITLE2 = 'Расписание команды "'+props.team_name+'"';

  let Data=null;
  if ((props.teamSchedule.listSchedule !== null)&(props.teamSchedule.listSchedule !== undefined)){
    Data=props.teamSchedule.listSchedule
  }

  let filteredDataTeams = null;

  if ((Data !== null)&(Data !== undefined)){ 
    filteredDataTeams = Data.filter( teamSchedule => {
    return (teamSchedule.awayTeam.name+teamSchedule.homeTeam.name+teamSchedule.utcDate).toLowerCase().includes(props.search.toLowerCase())
    } 
    )
  }
    
  let listItem = '<div>Нет данных</div>' ;

  if ((filteredDataTeams !== null )&(filteredDataTeams !== undefined)){
    listItem=filteredDataTeams.map(item => (
    <li key={item.competition.id}>Дата:{item.utcDate} Домашняя группа: {item.homeTeam.name}  (Гость:{item.awayTeam.name}) </li> ))
  }    

  let setStartDate = (date) => {
    props.selStartDate(date);
  }

  let setEndDate = (date) => {
    props.selEndDate(date);
  }

return (
	<div > 
    <h2>{TITLE1}</h2>
    <h2>{TITLE2}</h2>
    <div className="MyDateFilter">
      <span>Start Date:</span>
      <DatePicker
        dateFormat="mm-dd-yy"
        selected={props.startDate}
        onChange={setStartDate}
        
        selectsStart
        startDate = {props.startDate}

      />
      <DatePicker
        dateFormat="mm-dd-yy"
        selected={props.endDate}
        onChange={setEndDate}
        
        selectsEnd
        
        endDate = {props.endDate}
  
      />

    </div> 
    <ul>
      {listItem}
    </ul>
  </div>
)
}

export default withRouter(TeamSchedule);
