console.log("leagues");
console.log(error);
console.log(loaded);
if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!loaded) {
      return <div>Loading...</div>;
    } else {
    
	return( <div >
            <h2>{ TITLE }</h2>
           
            <h2>{ props.period }</h2>
            <div className={ s.list_items }>         
                ListLeagues 
            </div>
        </div>
  )//return      
}}

export default Leagues;
