import React from 'react';
import { NavLink } from 'react-router-dom';
import s from './Navbar.module.css';

const Navbar = (props) => {
 
  let sections=props.sections;
   console.log("sections=="+props.sections);
  let period="";
 // if (props.period!=="")  period="/"+props.period
  let menuItemList=sections.map((item,index) => ( 
              <li key={item.index}> <NavLink  className={s.menu__item} key={item.index}   to={item.href+"/"+props.period}>{item.title}</NavLink></li> ));

	return(
    <div className="nav-bar">
      <div className={s.hamburger_menu}>
        <input id={s.menu__toggle} type="checkbox" />
        <label className={s.menu__btn} for="menu__toggle">
          <span></span>
        </label>
        <nav>
          <ul className={s.menu__box}>
            {menuItemList}
          </ul>
        </nav> 
        <div className={s.title}><h1>ФУТБОЛЬНАЯ СТАТИСТИКА</h1></div> 
      </div>
    </div>
    )
}

export default Navbar;
