import React, {useState, useEffect} from 'react';
import { withRouter } from 'react-router-dom';
import s from './TeamSchedule.module.css';


 const TeamSchedule = (props) => {
 // const TITLE1 = 'Лига "'+props.league_name+'" '+props.period +' год';
  const TITLE2 = 'Расписание команды "'+props.team_name+'"';
 /* const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() =>{
  setLoaded(false);
  let API_KEY = process.env.REACT_APP_USER_TOKEN;
  let team_id = props.match.params.team_id;
  const apiUrl = "https://api.football-data.org/v2/competitions/"+league_id+"/teams?season="+period;
  fetch(apiUrl, {
          method: 'GET',
          headers: {
            'X-Auth-Token': API_KEY
          }
      })
  https://api.football-data.org/v2/competitions/PL/teams?season=2019
  fetch("https://api.football-data.org/v2/competitions/)
  .then(res => res.json())
  .then(
    json => {
      setLoaded(true);
      setTeamSchedule(json);
    },
    (error) =>{
      setLoaded(true);  
          setError(error);
    }
  )
  .catch(error => console.log('parsing failed', error))
  },[])
*/
  let Data=null;
  if ((props.teamSchedule.listSchedule !== null)&(props.teamSchedule.listSchedule !== undefined)){
    Data=props.teamSchedule.listSchedule
  }
  //const Data=teams;

let filteredDataTeams = null;

if ((Data !== null)&(Data !== undefined)){ 
   filteredDataTeams = Data.filter( teamSchedule => {
      return (teamSchedule.match_awayteam_name+teamSchedule.match_date+teamSchedule.match_time).toLowerCase().includes(props.search.toLowerCase())
   }
   )
}
    
let listItem=<div>Нет данных</div> 

if ((filteredDataTeams !== null )&(filteredDataTeams !== undefined)){
   listItem=filteredDataTeams.map(item => (
    <li key={item.match_id}>Дата:{item.match_date} {item.match_time}       (Гость:{item.match_awayteam_name}) </li> ))
}    

return (
	<div >
      <ul>
       	<h2>{TITLE2}</h2>
        <h2>{TITLE2}</h2>
         	{listItem}
      </ul>
  </div>
)
}

export default withRouter(TeamSchedule);
